////// meta var
var isPlaying = true;
var playAStep = false;
var selectedHeteroptera = 0;
var stepCount = 0;

////// imgs
var heteropterasImgs = [];

////// dom var
var dataInput;

////// pref var
var cellSize = 8;

////// syst var
var maze;
var heteropteras =[];

function preload () {
	// load one images per directions
	for (var i = 0; i < 4; i++) {
		heteropterasImgs[i] = loadImage("img/8-" + i + ".png");

	}
}

